import React from 'react';
import './StudentHome.css';
import TopBar from '../components/TopBar';
import InternshipCard from '../components/InternshipCard';
import ProfileOverview from '../components/ProfileOverview';

// Sample data; in a full implementation you’d fetch this from an API
const internships = [
  { id: 1, title: 'Software Engineering Intern', company: 'Google', location: 'Berlin', industry: 'Tech', duration: '3 Months', pay: 'Unpaid', date: '2 days ago', early: true },
  { id: 2, title: 'Marketing Intern', company: 'Unilever', location: 'Cairo', industry: 'FMCG', duration: '2 Months', pay: 'Unpaid', date: '4 days ago', early: false },
  { id: 3, title: 'Finance Intern', company: 'HSBC', location: 'Giza', industry: 'Banking', duration: '1 Month', pay: 'Paid', date: '1 week ago', early: false }
];

const StudentHome = () => (
  <div className="dashboard-container">
    <TopBar />

    <div className="home-content">
      <aside className="sidebar">
        <ProfileOverview />
      </aside>

      <main className="internships-area">
        <h2 className="section-title">Suggested for you!</h2>
        <div className="internship-cards">
          {internships.map((item, idx) => (
            <div
              key={item.id}
              className="card-wrapper"
              style={{ animationDelay: `${idx * 100}ms` }}
            >
              <InternshipCard {...item} />
            </div>
          ))}
        </div>
      </main>
    </div>

    <footer className="footer">GUC Internship Portal © 2025</footer>
  </div>
);

export default StudentHome;
