// File: src/pages/LoginPage.jsx

import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './LoginPage.css';
import scadLogo from '../assets/scad-logo.png';

const LoginPage = () => {
  const [email, setEmail]           = useState('');
  const [password, setPassword]     = useState('');
  const [fieldErrors, setFieldErrors] = useState({});
  const [formError, setFormError]   = useState('');

  const navigate = useNavigate();
  const { setUser } = useAuth();

  const validate = () => {
    const errs = {};
    if (!email) {
      errs.email = 'Email is required.';
    } else if (!/^\S+@\S+\.\S+$/.test(email)) {
      errs.email = 'Please enter a valid email.';
    }
    if (!password) {
      errs.password = 'Password is required.';
    }
    return errs;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const errs = validate();
    if (Object.keys(errs).length > 0) {
      setFieldErrors(errs);
      setFormError('');
      return;
    }
    setFieldErrors({});

    // Credential routing + setting context
    if (email === 'admin@guc.edu.eg' && password === 'admin') {
      setUser({ role: 'admin', name: 'Site Admin' });
      return navigate('/admin-home');
    }
    if (email === 'jobs@microsoft.com' && password === 'pass') {
      setUser({ role: 'company', name: 'Microsoft' });
      return navigate('/company-home');
    }
    if (email === 'yahia.yahia@guc.edu.eg' && password === 'pass') {
      setUser({ role: 'student', name: 'Yahia Hesham', status: 'pro' });
      return navigate('/student-home');
    }
    if (email === 'faculty@guc.edu.eg' && password === 'pass') {
      setUser({ role: 'faculty', name: 'Faculty Member' });
      return navigate('/faculty-home');
    }

    setFormError('Invalid email or password.');
  };

  return (
    <div className="login-page">
      <header className="login-topbar">
        <img src={scadLogo} alt="SCAD Logo" className="topbar-logo" />
        <Link to="/register-company" className="topbar-employer-btn">
          Become an Employer
        </Link>
      </header>

      <div className="login-main">
        <form className="login-form" onSubmit={handleSubmit} noValidate>
          <h2>Sign In</h2>

          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              id="email"
              type="email"
              placeholder="you@domain.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            {fieldErrors.email && (
              <span className="field-error">{fieldErrors.email}</span>
            )}
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              id="password"
              type="password"
              placeholder="••••••••"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            {fieldErrors.password && (
              <span className="field-error">{fieldErrors.password}</span>
            )}
          </div>

          <button type="submit" className="button-primary">
            Login
          </button>

          {formError && (
            <div className="form-message error">{formError}</div>
          )}
        </form>
      </div>
    </div>
  );
};

export default LoginPage;
