// File: src/pages/WelcomePage.jsx

import React from 'react';
import './WelcomePage.css';
import scadLogo from '../assets/scad-logo.png';

const companyLogos = [
  'deloitte-logo.png', 'etisalat-logo.png', 'henkel-logo.png', 'IBM-logo.png',
  'microsoft-logo.png', 'nestle-logo.png', 'pfizer-logo.png', 'pwc-logo.png',
  'QNB-logo.png', 'schneider-logo.png', 'siemens-logo.png', 'valeo-logo.png'
];

const WelcomePage = () => {
  return (
    <div className="welcome-container">
      <header className="welcome-topbar">
        <img src={scadLogo} alt="SCAD Logo" className="welcome-logo" />
        <button className="topbar-login">Login</button>
      </header>

      <div className="glass-box">
        <h1 className="welcome-title">Welcome to the GUC Internship Platform</h1>
        <p className="welcome-subtitle">
          Explore top internships, connect with industry leaders, and take charge of your career path â€” all in one place.
        </p>
        <div className="welcome-buttons">
          <button className="welcome-btn login">Login</button>
          <button className="welcome-btn signup">Sign Up as Company</button>
        </div>
      </div>

      <div className="company-tape">
        <div className="tape-track">
          {[...companyLogos, ...companyLogos].map((logo, index) => (
            <img
              key={index}
              src={require(`../assets/companies/${logo}`)}
              alt={logo.split('-')[0]}
              className="tape-logo"
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default WelcomePage;
