// File: src/pages/WelcomePage.jsx
import React, { useEffect, useRef } from 'react';
import './WelcomePage.css';
import scadLogo from '../assets/scad-logo.png';

const companyLogos = [
  'deloitte-logo.png','etisalat-logo.png','henkel-logo.png','IBM-logo.png',
  'microsoft-logo.png','nestle-logo.png','pfizer-logo.png','pwc-logo.png',
  'QNB-logo.png','schneider-logo.png','siemens-logo.png','valeo-logo.png'
];

const WelcomePage = () => {
  const marqueeRef = useRef(null);

  useEffect(() => {
    const track = marqueeRef.current;
    let scrollAmount = 0;
    const speed = 0.3; // lower is faster
    function step() {
      scrollAmount += speed;
      if (scrollAmount >= track.scrollWidth / 2) scrollAmount = 0;
      track.style.transform = `translateX(-${scrollAmount}px)`;
      requestAnimationFrame(step);
    }
    requestAnimationFrame(step);
  }, []);

  // duplicate for seamless loop
  const logos = [...companyLogos, ...companyLogos];

  return (
    <div className="welcome-wrapper">
      <nav className="welcome-nav">
        <img src={scadLogo} alt="SCAD" className="nav-logo" />
        <button className="nav-login-btn">Login</button>
      </nav>

      <main className="welcome-main">
        <div className="text-block">
          <h1>Launch Your Internship Journey!</h1>
          <p>Connect with leading companies, discover tailored opportunities, and propel your career forward.</p>
          <div className="cta-group" onClick={() => navigate('/login')}>
            <button className="cta-primary">Login</button>
            <button className="cta-secondary">Become an employer</button>
          </div>
          {/* small animated bubbles */}
          <div className="bubble bubble-1" />
          <div className="bubble bubble-2" />
          <div className="bubble bubble-3" />
        </div>
        <div className="visual-block">
          <div className="circle large" />
          <div className="circle medium" />
          <div className="circle small" />
        </div>
      </main>

      <div className="logo-marquee-container">
        <div className="logo-marquee" ref={marqueeRef}>
          {logos.map((logo, idx) => (
            <img
              key={idx}
              src={require(`../assets/companies/${logo}`)}
              alt={logo.replace('-logo.png', '')}
              className="marquee-logo"
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default WelcomePage;
