import React from 'react';
import './WelcomePage.css';
import { useNavigate } from 'react-router-dom';
import logo1 from '../assets/companies/microsoft-logo.png';
import logo2 from '../assets/companies/deloitte-logo.png';
import logo3 from '../assets/companies/etisalat-logo.png';
import logo4 from '../assets/companies/siemens-logo.png';
import logo5 from '../assets/companies/pwc-logo.png';

const WelcomePage = () => {
    const navigate = useNavigate();
  
    return (
      <div className="welcome-container">
        <h1 className="welcome-title">Welcome to the GUC Internship Portal</h1>
        <p className="welcome-subtitle">Discover opportunities, build your future, and connect with top companies around the world.</p>
  
        <div className="welcome-buttons">
          <button className="welcome-btn login" onClick={() => navigate('/login')}>Login</button>
          <button className="welcome-btn signup" onClick={() => navigate('/company-signup')}>Company Sign Up</button>
        </div>
  
        <div className="scroll-indicator">Scroll down to explore</div>
  
        <div className="company-tape">
          <div className="tape-track">
            <img src={logo1} alt="Company 1" className="tape-logo" />
            <img src={logo2} alt="Company 2" className="tape-logo" />
            <img src={logo3} alt="Company 3" className="tape-logo" />
            <img src={logo4} alt="Company 4" className="tape-logo" />
            {/* Repeating logos for infinite effect */}
            <img src={logo1} alt="Company 1" className="tape-logo" />
            <img src={logo2} alt="Company 2" className="tape-logo" />
            <img src={logo3} alt="Company 3" className="tape-logo" />
            <img src={logo4} alt="Company 4" className="tape-logo" />
          </div>
        </div>
      </div>
    );
  };
  
  export default WelcomePage;
